mapper.insert:
		 
		SELECT USER_SEQ
		FROM USERS
		WHERE USER_SEQ = #{USER_SEQ}
	
    

	MERGE INTO SULGO S1
	     USING (SELECT #{USER_SEQ} AS USER_SEQ, to_char(sysdate,'YY/MM/DD') AS SULGO_DATE FROM DUAL) U2
	        ON (S1.USER_SEQ = U2.USER_SEQ AND S1.SULGO_DATE = U2.SULGO_DATE)
	 WHEN MATCHED THEN
	    UPDATE SET
			SULGO_FLAG = #{SULGO_FLAG}
	 WHEN NOT MATCHED THEN
	    INSERT (
			USER_SEQ
			, SULGO_DATE
			, SULGO_FLAG
		)
		values(
			#{USER_SEQ}
			, sysdate
			, #{SULGO_FLAG}
		)
